# Combien y a-t-il de handle dans cette array ?
puts "Il y a #{journalists.length} handles dans cet array."

# Quel est le handle le plus court de cette liste ?
puts "Le handle le plus court est #{journalists.min {|a, b| a.length <=> b.length}} !"

# Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
five = []
    journalists.each do |i| i.slice(0)
        if i.length == 6
            five << i
        end
    end

puts "#{five.length} handles contiennent 5 caractères !"


# Combien commencent par une majuscule (première lettre juste après le @) ?

capital_letter = []
journalists.each do |i| 
    if /[[:upper:]]/.match(i[1])
        capital_letter << i
    end
end

puts "#{capital_letter.length} handles commencent par une majuscule !"

# Trie la liste de handle par ordre alphabétique
puts "Voici la liste par ordre alphabétique :"
puts journalists.sort

# Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
puts "Voici la liste par taille :"
puts journalists.sort{|i, j| i.length <=> j.length}

# Quelle est la position dans l'array de la personne @epenser ?
puts "La position de @epenser est #{journalists.index("@epenser")}"


# Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

handle_length = journalists.map {|handle| handle.size.to_i}
handle_length = handle_length.sort
a = handle_length.first
z = handle_length.last
    for i in a..z do
        handle_size = journalists.count {|handle| handle.size == i}
        if handle_size !=0
            puts "nombre de handles avec #{i} caractères est de #{handle_size} "
        end
    end
